import React,{useState} from 'react'
// import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import { Grid, TextField } from '@material-ui/core';
import axios from "axios"


const Login = () => {
  const [Login, setLogin] = useState();
  const [LoginStatus, setLoginStatus] = useState(false)

  function changeHandler(event){
    setLogin({...Login,
    [event.target.name]:event.target.value,
  })
  console.log(Login);
  }

 const handleSubmit =(e) =>{
   e.preventDefault();

   axios({
     mathod:"post",
     url:"http://localhost:5000/login",
     headers:{
      "Content-Type": "application/json",
        "Access-Control-Allow-Origin": "*",
     },
     data:Login,
    }).then((response)=>{
      if(!response.data.auth){
        setLoginStatus(false)
        if(response.data.wrong){
          document.getElementById("loginError").innerHTML ='invalid username or password'
        }else if(response.data.notUser){
          document.getElementById('loginError').innerHTML ='user not valid'
        }
      }else{
        setLoginStatus(true)
        console.log("respone",response.data);
        localStorage.setItem("token",response.data.token)
        localStorage.setItem('user')
      }

    })
 }

  const paperStyle = {
    padding: 20,
    height: "20rem",
    width: 300,
    margin: "20px auto",
    backgroundColor:"#ECF8E8",

  };
  const btStyle={
    margin:"30px 0"
  }


    return (
        <div style={{backgroundColor:"#E0F2FF"}} >
        <form onSubmit={handleSubmit}>
        <Grid style={{ position:"absolute", top:"50%",left:"50%",transform: 'translate(-50%, -50%)'}}>
        <Paper elavation={10} style={paperStyle}>
        <h3>Sign in</h3>


        <TextField name="username"   onChange={changeHandler} id="username" placeholder="enter username" fullWidth required/>
        <TextField name="password" onChange={changeHandler} id="password" placeholder="enter password" fullWidth required/>
        <button type="submit" style={btStyle} variant="contained" fullWidth required >sign in</button>

        </Paper>
        </Grid>
        </form>

        </div>
    )
}

export default Login
